import React from 'react';
import s from './Navbar.module.css';
import {NavLink} from "react-router-dom";


class Navbar extends React.Component{



    state = {
        editMode: true,
        product_open:[],
        product_id:0,
        close_all_product:false
    }



    close_all(){
        this.setState({
            close_all_product:true
        });
    }


        activateEditMode(id) {

        this.setState( {
            editMode: true,
            product_id: id
        }

        );
    }

    deactivateEditMode(id) {
        this.setState( {
            editMode: false,
            product_id:id
        } );
    }


    render() {

        var data_ = this.props.data.map((item, index) => {
        return (
            <div key={index}>
                <ul>{item.name}</ul>
                {
                    item.subcategory.map((subitem ) => {
                        return (
                        <div >
                             {
                    <div   >
                        <NavLink  onMouseEnter={ this.activateEditMode.bind(this, subitem.id) }
                                 // onMouseLeave={ this.deactivateEditMode.bind(this, subitem.id)}
                            to={subitem.name}
                         > {subitem.name}
                        </NavLink>
                    </div>
                }
                 {this.state.editMode && subitem.id === this.state.product_id &&
                    <div onMouseLeave={ this.deactivateEditMode.bind(this, subitem.id)}>
                            {
                                subitem.subcategoryproduct.map((product) => { return (
                                    <NavLink to={product.name}
                                        onMouseEnter={ this.activateEditMode.bind(this, subitem.id)}
                                        >
                                        {product.name}
                                   </NavLink>) }
                                )
                            }

                    </div>
                }
                        </div>
                        )
                    })
                }
            </div>
        )
    })
        debugger
        return (
            <nav className={s.nav}>
                <div className={s.item}>
                    <NavLink to="/empty" activeClassName={s.activeLink}>Empty </NavLink>
                </div>

                <div>
                    {data_}
                </div>
            </nav>
        )
    }

}

export default Navbar;
