import s from "./Navbar.module.css";
import {NavLink} from "react-router-dom";
import {base_url_category} from "../../api/api";
import Navbar from "./Navbar";
import React from "react";


class SubNav extends React.Component {


        base_url = "/api/category"

    state = {
        editMode: true,
        editModeProduct:false,
        product_last_id:0,
        product_open:[],
        product_id:0,
        close_all_product:false
    }



    close_all(){
        this.setState({
            close_all_product:true
        });
    }


        activateEditMode(id) {

        this.setState( {
            editMode: true,
            product_id: id
        }

        );
    }

    deactivateEditMode(id) {
        this.setState( {
            editMode: false,
            product_id:id
        } );
    }

           activateEditProductMode(id) {

        this.setState( {
            editModeProduct: true,
            product_last_id: id
        }

        );
    }

     deactivateEditProductMode(id) {

        this.setState( {
            editModeProduct: false,
            product_last_id: id
        }

        );
    }



    render() {


            var data_ = this.props.subitem_.subcategoryproduct.map((product) => {
                    return (

                            <button className={s.subitem_css}

                                onMouseEnter={this.activateEditMode.bind(this, this.props.subitem_.id)}
                            >
                                <NavLink to={base_url_category + this.props.item_.slug + "/" + this.props.subitem_.slug + "/" + product.slug}
                                         onMouseEnter={this.activateEditProductMode.bind(this, product.id)}
                                >
                                    {product.name} </NavLink>

                            </button>


                    )
                }
            )

        return (   {data_}  )
        }

}

export default SubNav;
