import React from 'react';
import s from './Navbar.module.css';
import {NavLink} from "react-router-dom";
import {base_url_category} from '../../api/api'

class Navbar extends React.Component{

    base_url = "/api/category"

    state = {
        editMode: true,
        editModeProduct:false,
        product_last_id:0,
        product_open:[],
        product_id:0,
        close_all_product:false
    }



    close_all(){
        this.setState({
            close_all_product:true
        });
    }


        activateEditMode(id) {

        this.setState( {
            editMode: true,
            product_id: id
        }

        );
    }

    deactivateEditMode(id) {
        this.setState( {
            editMode: false,
            product_id:id
        } );
    }

           activateEditProductMode(id) {

        this.setState( {
            editModeProduct: true,
            product_last_id: id
        }

        );
    }

     deactivateEditProductMode(id) {

        this.setState( {
            editModeProduct: false,
            product_last_id: id
        }

        );
    }


    render() {

        // var data_test = this.props.data.map((item, index) => {
        //     return (
        //      <div key={index}>
        //          <ul>{item.name}</ul>
        //          {
        //              item.subcategory.map((subitem) => {
        //
        //                   <button
        //                     className={s.subcategory_css}
        //                     onMouseEnter={ this.activateEditMode.bind(this, subitem.id) }
        //                     onMouseLeave={ this.deactivateEditMode.bind(this, null)}
        //                 > <NavLink to={base_url_category + item.slug + "/" + subitem.slug + "/"}> {subitem.name} </NavLink>
        //                 </button>
        //
        //
        //                  }
        //
        //
        //              )
        //
        //
        //              }
        //
        //      </div>
        //     )
        // })


        var data_ = this.props.data.map((item, index) => {
        return (
            <div key={index}>
                <ul>{item.name}</ul>
                {
                    item.subcategory.map((subitem ) => {
                        return (
                        <div >
                             {
                    <div   >
                        <button
                            className={s.subcategory_css}
                            onMouseEnter={ this.activateEditMode.bind(this, subitem.id) }
                            onMouseLeave={ this.deactivateEditMode.bind(this, null)}
                        > <NavLink to={base_url_category + item.slug + "/" + subitem.slug + "/"}> {subitem.name} </NavLink>
                        </button>
                    </div>
                }
                 {this.state.editMode && subitem.id === this.state.product_id &&
                    <div  onMouseLeave={ this.deactivateEditMode.bind(this, subitem.id)}>
                            {
                                subitem.subcategoryproduct.map((product) => { return (
                                    <div   >
                                    <button
                                        className={s.subitem_css}
                                        onMouseEnter={ this.activateEditMode.bind(this, subitem.id)}
                                        >
                                        <NavLink to={base_url_category + item.slug + "/" + subitem.slug + "/" + product.slug} onMouseEnter={this.activateEditProductMode.bind(this, product.id)}
                                        >
                                            {product.name} </NavLink>

                                   </button>
                                        {this.state.editModeProduct && product.id === this.state.product_last_id &&
                                            <div onMouseLeave={this.deactivateEditProductMode.bind(this, null)}>

                                            {
                                                product.subcategoryproduct_last.map((subproduct) => {

                                                    return <button>
                                                    <NavLink to={base_url_category +
                                                    item.slug + "/" + subitem.slug + "/" + product.slug + "/" + subproduct.slug} >

                                                     {subproduct.name} </NavLink>
                                                    </button>
                                                })
                                            }
                                            </div>
                                        }
                                    </div>
                                        )
                                    }
                                )
                            }

                    </div>
                }
                        </div>
                        )
                    })
                }
            </div>
        )
    })



        return (
            <nav className={s.nav}>
                <div className={s.item}>
                    <NavLink to="/empty" activeClassName={s.activeLink}>Empty </NavLink>
                </div>

                <div>
                    {data_}
                </div>
            </nav>
        )
    }

}

export default Navbar;
