import React from 'react';
import s from './Navbar.module.css';
import {NavLink} from "react-router-dom";


class Navbar extends React.Component{



    state = {
        editMode: true,
        editModeProduct:false,
        product_last_name:'',
        product_open:[],
        product_id:0,
        close_all_product:false
    }



    close_all(){
        this.setState({
            close_all_product:true
        });
    }


        activateEditMode(id) {

        this.setState( {
            editMode: true,
            product_id: id
        }

        );
    }

    deactivateEditMode(id) {
        this.setState( {
            editMode: false,
            product_id:id
        } );
    }

           activateEditProductMode(name) {
            alert(name)
        this.setState( {
            editModeProduct: true,
            product_last_name: name
        }

        );
    }

     deactivateEditProductMode(name) {

        this.setState( {
            editModeProduct: false,
            product_last_name: name
        }

        );
    }


    render() {

        var data_ = this.props.data.map((item, index) => {
        return (
            <div key={index}>
                <ul>{item.name}</ul>
                {
                    item.subcategory.map((subitem ) => {
                        return (
                        <div >
                             {
                    <div   >
                        <button  onMouseEnter={ this.activateEditMode.bind(this, subitem.id) }
                                 onMouseLeave={ this.deactivateEditMode.bind(this, null)}
                        > <NavLink to={subitem.name}> {subitem.name} </NavLink>
                        </button>
                    </div>
                }
                 {this.state.editMode && subitem.id === this.state.product_id &&
                    <div onMouseLeave={ this.deactivateEditMode.bind(this, subitem.id)}>
                            {
                                subitem.subcategoryproduct.map((product) => { return (
                                    <div>
                                    <button
                                        onMouseEnter={ this.activateEditMode.bind(this, subitem.id)

                                        }
                                        >
                                        <NavLink to={product.name}>  {product.name} </NavLink>

                                   </button>
                                        {product.subcategoryproduct_last.map((subproduct)=> {return <div> {subproduct.name} </div>})}
                                    </div>
                                        )
                                    }
                                )
                            }

                    </div>
                }


                        </div>
                        )
                    })
                }
            </div>
        )
    })
        debugger
        return (
            <nav className={s.nav}>
                <div className={s.item}>
                    <NavLink to="/empty" activeClassName={s.activeLink}>Empty </NavLink>
                </div>

                <div>
                    {data_}
                </div>
            </nav>
        )
    }

}

export default Navbar;
